def prefix = "krishsoap"
def suffix = "@yopmail.com"

// create the list of available characters  

   def availChars = []  
   ('a'..'z').each { availChars << it.toString()}
   //('A'..'Z').each { availChars << it.toString()}  
   // even it out to about the same odds of getting a char or a number  
   //3.times { (1..9).each { availChars << it.toString() } }  

   def generateRandomString = { length ->   
   def max = availChars.size      
   def rnd = new Random()  
   def sb = new StringBuilder()  
   length.times { sb.append(availChars[rnd.nextInt(max)]) }  
   sb.toString()  
   }

def email = prefix + generateRandomString(6) + suffix

//testRunner.testCase.testSuite.project.addProperty("c_msisdn")
testRunner.testCase.testSuite.project.setPropertyValue("primaryEmail", email)
log.info "email = " + email

